import { useContext } from "react";
import { Helmet } from "react-helmet-async";
import { Store } from "../Store";
import { Link } from "react-router-dom";
import MessageBox from '../components/MessageBox'
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import ListGroup from "react-bootstrap/ListGroup";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import axios from "axios";



export default function CartScreen() {
  const { state, dispatch: contextDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;

  const updateCartHandler = async (item, quantity) => {
    const { data } = await axios.get(`/api/products/${item._id}`)

    if (data.countInStock < quantity) {
      window.alert('Sorry. The product is out of stock');
      return;
    }

    contextDispatch({
      type: 'CART_ADD_ITEM',
      payload: { ...item, quantity },
    });
  }

  return (
    <div>
      <Helmet>
        <title>Amazona - Shopping Cart</title>
      </Helmet>
      <h1 className="text-center">Shopping Cart</h1>
      <Row>

        <Col md={8}>
          {cartItems.length === 0 ? (
            <MessageBox>
              Cart is empty. <Link to="/">Go Shopping</Link>
            </MessageBox>
          ) : (
            <ListGroup>
              {cartItems.map((item) => (
                <ListGroup.Item key={item._id}>
                  <Row className="align-items-center">

                    <Col md={4}>
                      <img
                        src={item.image}
                        alt={item.name}
                        className="img-fluid rounded img-thumbnail"
                      ></img>{' '}
                      <Link to={`/product/${item.slug}`}>{item.name}</Link>
                    </Col>

                    <Col md={3}>

                      <Button
                        onClick={() => updateCartHandler(item, item.quantity - 1)}
                        disabled={item.quantity === 1}>
                        <i className="fas fa-minus-circle "></i>
                      </Button>{' '}

                      <span>{item.quantity}</span>{' '}

                      <Button
                        onClick={() => updateCartHandler(item, item.quantity + 1)}
                        disabled={item.quantity === item.countInStock}>
                        <i className="fas fa-plus-circle "></i>
                      </Button>

                    </Col>

                    <Col md={3}>S/ {item.price}</Col>

                    <Col md={2}>
                      <Button
                        onClick={() => removeItemHandler(item)}>
                        <i className="fas fa-trash"></i>
                      </Button>
                    </Col>

                  </Row>
                </ListGroup.Item>
              ))}
            </ListGroup>
          )
          }
        </Col>

        <Col md={4}>
          <Card>
            <Card.Body>
              <ListGroup variant="flush">

                <ListGroup.Item>
                  <h3>
                    Subtotal ( {cartItems.reduce((accumulator, currentItem) =>
                      accumulator + currentItem.quantity, 0)} {' '} items in the cart) : S/{' '}
                    {cartItems.reduce((accumulator, currentItem) => accumulator + currentItem.price * currentItem.quantity, 0)}
                  </h3>
                </ListGroup.Item>
                <ListGroup.Item>
                  <div className="d-grid">
                    <Button
                      type="button"
                      className="btn-block"
                      disabled={cartItems.length === 0}
                    >Procced to checkout</Button>
                  </div>
                </ListGroup.Item>
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>

      </Row>

    </div>
  );

}
